'use strict';

var expect = require('chai').expect;
var templatesjs = require('../index.js');

describe('#templatesjs', function() {
	
    it('should replace the <%user%> tag with "imtiaz" using renderSync()', function() {
		templatesjs.setSync("<%user%>");
        var result = templatesjs.renderSync("user", "imtiaz");
        expect(result).to.equal('imtiaz');
    });

    it('should replace the <%user%> tag with "imtiaz" using render()', function() {
		templatesjs.set("<%user%>", function(err,data){
			if(err) throw err;
			templatesjs.render("user", "imtiaz", function(err,result){
				if(err) throw err;
				expect(result).to.equal('imtiaz');
				
			});
		});
    });

    it('should replace the <%user%> tag with "imtiaz" using renderSync() when style specified as "case" ', function() {
		templatesjs.setSync("<%user%>");
        var result = templatesjs.renderSync("user", "iMTiaz", "case");
        expect(result).to.equal('imtiaz');
    });

    it('should replace the <%user%> tag with "imtiaz" using render() when style specified as "case"', function() {
		templatesjs.set("<%user%>", function(err,data){
			if(err) throw err;
			templatesjs.render("user", "iMtiaz", "case", function(err,result){
				if(err) throw err;
				
				expect(result).to.equal('imtiaz');
			});
		});
    });

    it('should replace the <%user%> tag with "IMTIAZ" using renderSync() when style specified as "CASE"', function() {
		templatesjs.setSync("<%user%>");
        var result = templatesjs.renderSync("user", "imtiaz", "CASE");
        expect(result).to.equal('IMTIAZ');
    });

    it('should replace the <%user%> tag with "Imtiaz" using renderSync() when style specified as "Case"', function() {
		templatesjs.setSync("<%user%>");
        var result = templatesjs.renderSync("user", "imtiaz","Case");
        expect(result).to.equal('Imtiaz');
    });

    it('should replace the "hello <%user.name%>" text with "imtiaz"', function() {
		templatesjs.setSync("hello <%user.name%>");
        var result = templatesjs.renderSync("user", {name:"imtiaz"});
        expect(result).to.equal('hello imtiaz');
    });

    it('should replace the <%user[]%> tag with "ImtiazChowdhury"', function() {
		templatesjs.setSync("<%user[]%>");
        var result = templatesjs.renderSync("user", ["Imtiaz", "Chowdhury"]);
        expect(result).to.equal('ImtiazChowdhury');
    });

    it('should replace the <%user[1]%> tag with "Chowdhury"', function() {
		templatesjs.setSync("<%user[1]%>")
        var result = templatesjs.renderSync("user", ["Imtiaz", "Chowdhury"]);
        expect(result).to.equal('Chowdhury');
    });

    it('should replace the <%user[] {<span>*</span>}%> tag with "<span>Imtiaz</span><span>Chowdhury</span>"', function() {
		templatesjs.setSync("<%user[] {<span>*</span>}%>")
        var result = templatesjs.renderSync("user", ["Imtiaz", "Chowdhury"]);
        expect(result).to.equal('<span>Imtiaz</span><span>Chowdhury</span>');
    });

    it('should replace the <%user[1] {<span>*</span>}%> tag with "<span>Chowdhury</span>"', function() {
		templatesjs.setSync("<%user[1] {<span>*</span>}%>")
        var result = templatesjs.renderSync("user", ["Imtiaz", "Chowdhury"]);
        expect(result).to.equal('<span>Chowdhury</span>');
    });

    it('should replace the <%user[1,3]%> tag with "ChowdhuryMadihaMahboob"', function() {
		templatesjs.setSync("<%user[1,3]%>");
        var result = templatesjs.renderSync("user", ["Imtiaz", "Chowdhury","Madiha", "Mahboob"]);
        expect(result).to.equal('ChowdhuryMadihaMahboob');
    });

    it('should replace the <%user[1,3] {<span>*</span>}%> tag with "<span>Chowdhury</span><span>Madiha</span><span>Mahbbob</span>"', function() {
       templatesjs.setSync("<%user[1,3] {<span>*</span>}%>")
	    var result = templatesjs.renderSync("user", ["Imtiaz", "Chowdhury","Madiha", "Mahbbob"]);
        expect(result).to.equal('<span>Chowdhury</span><span>Madiha</span><span>Mahbbob</span>');
    });
    it('should replace the <%include test.txt%> tag with "templatesjs"', function() {
		templatesjs.dir = "./test/"
	    var result = templatesjs.setSync("<%include test.txt %>");
	   
       expect(result).to.equal('templatesjs');
    });
	
	it('should replace the <%firstname%><%lastname%><%email%> with "JohnDoeJohn@Doe.com" using renderAllSync()', function() {
	   templatesjs.setSync("<%firstname%><%lastname%><%email%>");
	   var list = {
		   firstname:"John",
		   lastname:"Doe",
		   email:"John@Doe.com"
	   }
	   var result = templatesjs.renderAllSync(list);
       expect(result).to.equal('JohnDoeJohn@Doe.com');
    });
	
	it('should replace the <%firstname%><%lastname%><%email%> with "JohnDoeJohn@Doe.com" using renderAll()', function() {
	   templatesjs.setSync("<%firstname%><%lastname%><%email%>");
	   var list = {
		   firstname:"John",
		   lastname:"Doe",
		   email:"John@Doe.com"
	   }
	   templatesjs.renderAll(list, function(err,result){
		   if(err) throw err;
		   expect(result).to.equal('JohnDoeJohn@Doe.com');
	   });
    });
	
});